import React from 'react';
import { ReactComponent as CarSvg } from '../images/araba.svg'; // Assuming car.svg is in the same directory
import Yollar from '../images/yollar.png'
import constants from '../backend/constants';
import backend from '../backend';
import { CarPosition } from '../backend/types';
import { CarPositionAndSpeedLevel } from '../backend/types';
import { CarPositionWithSpeed } from '../backend/types';
import { CarSpeedLevel } from '../backend/types';
import { useState, useEffect } from 'react';


const MyComponent = () => {
    const [carPosAndSpeed, setCarPosAndSpeed] = useState<CarPositionAndSpeedLevel>({
        carPosition: { x: 0, y: 0, orientation: 0 },
        speedLevel: CarSpeedLevel.SLOW,
      });
    
      useEffect(() => {
        // Fetch initial data
        backend.getInitialCarPositionAndSpeed().then(setCarPosAndSpeed);
    
        // Set up event listeners
        const handleCarPositionAndSpeedChanged = (e: CustomEvent) => {
          setCarPosAndSpeed(e.detail);
        };
        const handleSpeedLevelChanged = (e: CustomEvent) => {
          setCarPosAndSpeed(prev => ({ ...prev, speedLevel: e.detail.speedLevel }));
        };
        
        document.addEventListener('carPositionAndSpeedChanged', handleCarPositionAndSpeedChanged);
        document.addEventListener('speedLevelChanged', handleSpeedLevelChanged);
    
        // Clean up event listeners on component unmount
        return () => {
          document.removeEventListener('carPositionAndSpeedChanged', handleCarPositionAndSpeedChanged);
          document.removeEventListener('speedLevelChanged', handleSpeedLevelChanged);
        };
      }, []);


    return (
        <div style={{ display: 'flex', marginLeft: '33%', marginTop:'5%' }}>
          <div style={{ position: 'relative', width: '600px', height: '600px', backgroundColor: 'green' }}>
            <img 
              src={Yollar} 
              alt="road" 
              style={{ 
                width: '100%', 
                height: '100%',
                position: 'absolute',
                zIndex: 1
              }}
            />
            <CarSvg 
              style={{ 
                width: '25px', 
                height: '25px',
                position: 'absolute',
                left: carPosition.x, 
                top: carPosition.y,
                transform: `rotate(${carPosition.orientation}deg)`,
                zIndex: 2
              }} 
            />
            <button 
                style={{
                    marginLeft: '475px',
                    marginTop:'20px'
                }}
            >
                Şu Anki Hız: {suankihiz}
            </button>
          </div>
          <div style={{display:'flex', marginLeft:'25px'}}>
            x: {carPosition.x}, y: {carPosition.y}, orientation: {carPosition.orientation}, speed: 0
            
          </div>
        </div>
      );
}

export default MyComponent;
