import React from 'react';
import { ReactComponent as CarSvg } from '../images/araba.svg'; // Assuming car.svg is in the same directory
import Yollar from '../images/yollar.png'
const MyComponent = () => {
    var carPosition = { left: '200px', top: '200px' };

    return (
        <div style={{ display: 'flex' }}>
          <div style={{ position: 'relative', width: '500px', height: '500px', marginRight: '20px' }}>
            <img 
              src="./road.png" 
              alt="road" 
              style={{ 
                width: '100%', 
                height: '100%',
                position: 'absolute',
                zIndex: 1
              }}
            />
            <CarSvg 
              style={{ 
                width: '100px', 
                height: '100px',
                position: 'absolute',
                left: carPosition.left, 
                top: carPosition.top,
                zIndex: 2
              }} 
            />
          </div>
          <div>
            Car Position: 
            <br/>
            Left: {carPosition.left}
            <br/>
            Top: {carPosition.top}
          </div>
        </div>
      );
}

export default MyComponent;
